import json


# notes:
# 10 - 99.01%
# 20 - 99.01%
# 50 - 100%
# 100 - 99.01%

def compareMatchingCharacters(str1, str2): 
    c, j = 0, 0
    for i in range(len(str1)):
        if str1[i] == str2[i]:
            c += 1
    # print ('No. of matching characters are : ', c)
    return c


teensy_json = '{"m":0,"d":["","","","","","","","",""]}'
message = ""
addLeadingOne = True

# print(len([120, 200, 250, 300, 350, 400, 450, 500, 550, 600, 700, 800, 900, 1000, 1100, 1200, 1300, 1400, 1500, 1600, 1700, 2000, 2500, 3000, 3500, 4000, 4500, 5000, 5500, 6000, 6500, 7000, 7500]))
teensy_dict = json.loads(teensy_json)
if(addLeadingOne):
    message = "1"+message
# print("Length of message: ", len(message))
newmessagematch = []
for teensy_message in teensy_dict['d']:
    newmessagematch.append(compareMatchingCharacters(message, teensy_message))
maxMessageMatch = max(newmessagematch)
print("Number of Matching Characters: ", maxMessageMatch)
print("Max Accuracy: ", round(maxMessageMatch/len(message)*100,2), "%")
print("Len of new message match: ", len(newmessagematch))
for i in range(len(newmessagematch)):
    newmessagematch[i] = round(newmessagematch[i]/len(message)*100,2)
print("Individual Accuracy: ", newmessagematch)